// App.js
import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");
  const [language, setLanguage] = useState("en");

  const apiKey = process.env.REACT_APP_API_KEY;

  console.log("API Key:", apiKey); // Verifica se a chave está sendo carregada corretamente

  const getWeather = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric&lang=${language}` // Inclui o parâmetro de linguagem
      );
      console.log(response.data); // Exibe a resposta da API no console
      setWeather(response.data);
      setError("");
    } catch (err) {
      console.error(err); // Exibe o erro no console
      if (err.response) {
        // A resposta da API foi recebida, mas o status não é 200
        setError(err.response.data.message); // Mensagem de erro da API
      } else {
        // Não foi possível conectar à API
        setError("Erro de conexão. Tente novamente mais tarde.");
      }
      setWeather(null);
    }
  };

  // Função para traduzir a descrição do clima
  const translateDescription = (description) => {
    const translations = {
      "clear sky": "céu limpo",
      "few clouds": "poucas nuvens",
      "scattered clouds": "nuvens dispersas",
      "broken clouds": "nuvens fragmentadas",
      "shower rain": "chuva leve",
      rain: "chuva",
      thunderstorm: "tempestade",
      snow: "neve",
      mist: "neblina",
      // Adicione mais traduções conforme necessário
    };

    return translations[description] || description;
  };

  return (
    <div className="app">
      <h1>
        {language === "en" ? "Weather in" : "Clima em"} {city}
      </h1>

      {weather && (
        <div className="card">
          <h2>{weather.main.temp} °C</h2>
          <p>
            {language === "en"
              ? weather.weather[0].description
              : translateDescription(weather.weather[0].description)}
          </p>
          <img
            src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
            alt={weather.weather[0].description}
          />
        </div>
      )}

      <div className="input-container">
        <div className="weather-info">
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder={language === "en" ? "Enter city" : "Digite a cidade"}
          />
          <button onClick={getWeather}>
            {language === "en" ? "Get Weather" : "Obter Clima"}
          </button>
        </div>

        <div>
          <label htmlFor="language-select">Select Language:</label>
          <select
            id="language-select"
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="en">English</option>
            <option value="pt">Português</option>
          </select>
        </div>
      </div>

      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default App;
