import React, { useState } from "react";
import axios from "axios";
import "./App.css";

const App = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");

  const apiKey = process.env.REACT_APP_API_KEY;

  console.log("API Key:", apiKey); // Verifica se a chave está sendo carregada corretamente

  const getWeather = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`
      );
      console.log(response.data); // Exibe a resposta da API no console
      setWeather(response.data);
      setError("");
    } catch (err) {
      console.error(err); // Exibe o erro no console
      if (err.response) {
        // A resposta da API foi recebida, mas o status não é 200
        setError(err.response.data.message); // Mensagem de erro da API
      } else {
        // Não foi possível conectar à API
        setError("Erro de conexão. Tente novamente mais tarde.");
      }
      setWeather(null);
    }
  };

  return (
    <div className="app">
      <h1>Weather in {city}</h1>

      {weather && (
        <div className="card">
          <h2>{weather.main.temp} °C</h2>
          <p>{weather.weather[0].description}</p>
          <img
            src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
            alt={weather.weather[0].description}
          />
        </div>
      )}

      <div className="weather-info">
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city"
        />
        <button onClick={getWeather}>Get Weather</button>
      </div>

      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default App;
