// App.js
import React, { useState } from "react";
import axios from "axios";
import Lottie from "lottie-react"; // Importando o Lottie
import sunAnimation from "./animations/sun.json"; // Certifique-se de ter esse arquivo na pasta correta
import rainAnimation from "./animations/rain.json"; // Certifique-se de ter esse arquivo na pasta correta
import "./App.css";

const App = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");
  const [language, setLanguage] = useState("en");

  const apiKey = process.env.REACT_APP_API_KEY;

  const getWeather = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric&lang=${language}`
      );
      setWeather(response.data);
      setError("");
    } catch (err) {
      if (err.response) {
        setError(err.response.data.message);
      } else {
        setError("Erro de conexão. Tente novamente mais tarde.");
      }
      setWeather(null);
    }
  };

  // Função para traduzir a descrição do clima
  const translateDescription = (description) => {
    const translations = {
      "clear sky": "céu limpo",
      "few clouds": "poucas nuvens",
      "scattered clouds": "nuvens dispersas",
      "broken clouds": "nuvens fragmentadas",
      "shower rain": "chuva leve",
      rain: "chuva",
      thunderstorm: "tempestade",
      snow: "neve",
      mist: "neblina",
    };
    return translations[description] || description;
  };

  // Função para determinar qual animação usar com base nas condições do clima
  const getAnimation = (condition) => {
    switch (condition) {
      case "Clear":
        return sunAnimation; // Arquivo de animação para sol
      case "Rain":
      case "Drizzle":
      case "Shower Rain":
        return rainAnimation; // Arquivo de animação para chuva
      // Adicione mais condições e animações conforme necessário
      default:
        return null; // Sem animação para outras condições
    }
  };

  return (
    <div className="app">
      <h1>
        {language === "en" ? "Weather in" : "Clima em"} {city}
      </h1>

      {weather && (
        <div className="card">
          <Lottie
            animationData={getAnimation(weather.weather[0].main)} // Seleciona a animação com base na condição do clima
            loop
            style={{ width: "200px", height: "200px" }} // Define o tamanho da animação
          />
          <h2>{weather.main.temp} °C</h2>
          <p>
            {language === "en"
              ? weather.weather[0].description
              : translateDescription(weather.weather[0].description)}
          </p>
        </div>
      )}

      <div className="input-container">
        <div className="weather-info">
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder={language === "en" ? "Enter city" : "Digite a cidade"}
          />
          <button onClick={getWeather}>
            {language === "en" ? "Get Weather" : "Ver clima"}
          </button>
        </div>

        <div>
          <label htmlFor="language-select">Select language:</label>
          <select
            id="language-select"
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="en">English</option>
            <option value="pt">Português</option>
          </select>
        </div>
      </div>

      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default App;
