// App.js
import React, { useState } from "react";
import axios from "axios";
import Lottie from "lottie-react";
import "./App.css";

// Importando as animações
import dayStorm from "./animations/day_storm.json";
import moonClear from "./animations/moon_clear.json";
import moonCloudy from "./animations/moon_cloudy.json";
import nightSnow from "./animations/night_snow.json";
import partlyCloudy from "./animations/partly_cloudy.json";
import rainyDay from "./animations/rainy_day.json";
import rainyNight from "./animations/rainy_night.json";
import snow from "./animations/snow.json";
import storm from "./animations/storm.json";
import sunnySnow from "./animations/sunny_snow.json";
import sunny from "./animations/sunny.json";
import thunder from "./animations/thunder.json";
import windy from "./animations/windy.json";

const App = () => {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");
  const [language, setLanguage] = useState("en");

  const apiKey = process.env.REACT_APP_API_KEY;

  const getWeather = async () => {
    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric&lang=${language}`
      );
      setWeather(response.data);
      setError("");
    } catch (err) {
      if (err.response) {
        setError(err.response.data.message);
      } else {
        setError("Erro de conexão. Tente novamente mais tarde.");
      }
      setWeather(null);
    }
  };

  // Função para selecionar a animação com base no tipo de clima
  const getWeatherAnimation = (description) => {
    const isNight = weather && weather.weather[0].icon.includes("n");

    switch (description) {
      case "clear sky":
        return isNight ? moonClear : sunny;
      case "few clouds":
        return isNight ? moonCloudy : partlyCloudy;
      case "scattered clouds":
      case "broken clouds":
        return partlyCloudy;
      case "shower rain":
      case "rain":
        return isNight ? rainyNight : rainyDay;
      case "thunderstorm":
        return isNight ? storm : dayStorm;
      case "snow":
        return isNight ? nightSnow : sunnySnow;
      case "mist":
        return windy;
      case "windy":
        return windy;
      default:
        return sunny; // Animação padrão
    }
  };

  return (
    <div className="app">
      <h1>
        {language === "en" ? "Weather in" : "Clima em"} {city}
      </h1>

      {weather && (
        <div className="card">
          <h2>{weather.main.temp} °C</h2>
          <p>
            {language === "en"
              ? weather.weather[0].description
              : translateDescription(weather.weather[0].description)}
          </p>
          {/* Exibir a animação baseada no clima */}
          <Lottie
            animationData={getWeatherAnimation(weather.weather[0].description)}
            style={{ width: 150, height: 150 }}
          />
        </div>
      )}

      <div className="input-container">
        <div className="weather-info">
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder={language === "en" ? "Enter city" : "Digite a cidade"}
          />
          <button onClick={getWeather}>
            {language === "en" ? "Get Weather" : "Obter Clima"}
          </button>
        </div>

        <div>
          <label htmlFor="language-select">Select Language:</label>
          <select
            id="language-select"
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="en">English</option>
            <option value="pt">Português</option>
          </select>
        </div>
      </div>

      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default App;
